function regulateEventGrid(theWidget) {
    // COLLECT ALL DATA
    const defs = datasources.Home_fetchEventDefinitions.latestData() && datasources.Home_fetchEventDefinitions.latestData().results.DATA;
    const entities = datasources.Home_fetchEntities.latestData() && datasources.Home_fetchEntities.latestData().results.DATA;
    const areas = datasources.Home_fetchAreas.latestData() && datasources.Home_fetchAreas.latestData().results.DATA;
    const event_types = datasources.Home_fetchEventDefinitionTypes.latestData() && datasources.Home_fetchEventDefinitionTypes.latestData().results.DATA;
    const entity_types = datasources.Home_fetchEntityTypes.latestData() && datasources.Home_fetchEntityTypes.latestData().results.DATA;

    window.addEventListener("hashchange", (e) => {
        window.hasSubscribedToActiveButton = false;
        window.hasSubscribedToAllButton = false;
        window.hasSubscribedToEventSearch = false;
        window.hasSubscribedToEventTriggered = false;
        window.hasSubscribedToSelectedEntity = false;
        window.hasSubscribedToSelectedArea = false;
    })

    // SEARCH INPUT LOGIC
    if (!window.hasSubscribedToEventSearch) {
        window.hasSubscribedToEventSearch = true;
        $('#search-events-input').on('input', window.debounce(() => {
            const search = $("#search-events-input").val();
            const filters = theWidget.widget.query.query.FILTERS;
            if (search.length > 0) {
                if (filters && filters.length > 0) {
                    for (let i = 0; i < filters.length; i++) {
                        for (let j = filters[i].length - 1; j >= 0; j--) {
                            if (filters[i][j].hasOwnProperty('RE')) {
                                filters[i].splice(j, 1);
                            }
                        }
                    filters[i].push({ RE: [{ name: search }]})
                    }
                } else {
                    theWidget.widget.query.matches('name', search);
                }  
            } else {
                for (let i = 0; i < filters.length; i++) {
                    for (let j = filters[i].length - 1; j >= 0; j--) {
                        if (filters[i][j].hasOwnProperty('RE')) {
                            filters[i].splice(j, 1);
                        }
                    }
                }
            }
            if (theWidget.widget.query) {
                datasources.Home_events.sendData({ method: 'GET', query: theWidget.widget.query })
            }
        }, 250))
    }

    // SELECT ACTIVE EVENTS BUTTON LOGIC
    if (!window.hasSubscribedToActiveButton) {
        window.hasSubscribedToActiveButton = true;
        $('#activeEventsButton').on('click', () => {
            $('#search-events-input').val('');
            if ($('#activeEventsButton').hasClass('active')) { return };
            $('#activeEventsButton').addClass('active');
            $('#allEventsButton').removeClass('active');
            const filters = theWidget.widget.query.query.FILTERS;
            if (filters && filters.length > 0) {
                for (let i = 0; i < filters.length; i++) {
                    for (let j = filters[i].length - 1; j >= 0; j--) {
                        if (filters[i][j].hasOwnProperty('EQ')) {
                            if (!alreadyContainsActive(filters[i][j].EQ)) {
                                filters[i][j].EQ.push({ active: true })
                            }
                        } else {
                            filters[i].push({ EQ: [{ active: true }] })
                        }
                    }
                }
            } else {
                theWidget.widget.query.equalTo('active', true);
            }  
            if (theWidget.widget.query) {
                datasources.Home_events.sendData({ method: 'GET', query: theWidget.widget.query })
            }
        })
    }

    // SELECT ALL EVENTS BUTTON LOGIC
    if (!window.hasSubscribedToAllButton) {
        window.hasSubscribedToAllButton = true;
        $('#allEventsButton').on('click', () => {
            $('#search-events-input').val('');
            if ($('#allEventsButton').hasClass('active')) { return };
            $('#allEventsButton').addClass('active');
            $('#activeEventsButton').removeClass('active');
            const filters = theWidget.widget.query.query.FILTERS;
            if (filters && filters.length > 0) {
                for (let i = filters.length - 1; i >= 0; i--) {
                    for (let j = filters[i].length - 1; j >= 0; j--) {
                        if (filters[i][j].hasOwnProperty('EQ')) {
                            if (alreadyContainsActive(filters[i][j].EQ)) {
                                for (let k = 0; k < filters[i][j].EQ.length; k++) {
                                    if (filters[i][j].EQ[k].hasOwnProperty('active')) {
                                        filters[i][j].EQ.splice(k, 1);
                                    }
                                }
                                if (filters[i][j].EQ.length === 0) {
                                    delete filters[i][j].EQ;
                                }
                            }
                        }
                        if (Object.keys(filters[i][j]).length === 0) {
                            delete filters[i][j]
                        }
                    }
                    if (!filters[i][0]) {
                        filters.splice(i, 1);
                    }
                    if (filters.length === 0) {
                        delete theWidget.widget.query.query.FILTERS;
                    }
                }
            } 
            if (theWidget.widget.query) {
                datasources.Home_events.sendData({ method: 'GET', query: theWidget.widget.query })
            }
        })
    }

    // NEW EVENT TRIGGERED LOGIC
    if (!window.hasSubscribedToEventTriggered) {
        datasources.Home_eventTriggered.latestData.subscribe(() => {
            datasources.Home_events.sendData({ method: 'GET', query: theWidget.widget.query })
        })
        window.hasSubscribedToEventTriggered = true;
    }

    // SELECT ENTITY FROM VISUALIZATION
    if (!window.hasSubscribedToSelectedEntity) {
        datasources.Home_selected_entity.latestData.subscribe(() => {
            const selected = datasources.Home_selected_entity.latestData();
            if (selected !== 'null') {
                $('#search-events-input').val('');
                delete theWidget.widget.query.query.FILTERS;
                theWidget.widget.query.equalTo('entity_id', selected);
                if (activeSelected()) {
                    theWidget.widget.query.equalTo('active', true);
                }
                const query = CB_PORTAL.ClearBlade.Query();
                query.equalTo('entity2_id', selected);
                if (activeSelected()) {
                    query.equalTo('active', true);
                }
                theWidget.widget.query.or(query);
            } else {
                $('#search-events-input').val('');
                delete theWidget.widget.query.query.FILTERS;
                if (activeSelected()) { theWidget.widget.query.equalTo('active', true)}
            }
            if (theWidget.widget.query){
                datasources.Home_events.sendData({ method: 'GET', query: theWidget.widget.query })
            }
        })
        window.hasSubscribedToSelectedEntity = true;
    }

    // SELECT AREA FROM  VISUALIZATION
    if (!window.hasSubscribedToSelectedArea) {
        datasources.Home_selected_area.latestData.subscribe(() => {
            const selected = datasources.Home_selected_area.latestData();
            if (selected !== 'null') {
                $('#search-events-input').val('');
                delete theWidget.widget.query.query.FILTERS;
                const matchingIds = defs && defs.filter((d) => d.area_id === selected).map((n) => n.item_id);
                theWidget.widget.query.equalTo('event_definition_id', matchingIds[0] ? matchingIds[0] : '');
                if (activeSelected()) {
                    theWidget.widget.query.equalTo('active', true);
                }
                if (matchingIds && matchingIds.length > 0) {
                    for (let i = 1; i < matchingIds.length; i++) {
                        const q = CB_PORTAL.ClearBlade.Query();
                        q.equalTo('event_definition_id', matchingIds[i]);
                        if (activeSelected()) {
                            q.equalTo('active', true);
                        }
                        theWidget.widget.query.or(q);
                    }
                }
            } else {
                $('#search-events-input').val('');
                delete theWidget.widget.query.query.FILTERS;
                if (activeSelected()) { theWidget.widget.query.equalTo('active', true)}
            }
            if (theWidget.widget.query){
                datasources.Home_events.sendData({ method: 'GET', query: theWidget.widget.query })
            }
        })
        window.hasSubscribedToSelectedArea = true;
    }

    // UPDATE FILTERS TO ADD APPROPIATE IDS FOR SEARCHED NAMES AND ALSO ADD ACTIVE/ALL LOGIC
    if (theWidget.widget.query.query.FILTERS) {
        const filters = theWidget.widget.query.query.FILTERS;
        for (let j = 0; j < filters.length; j++) {
            for (let i = 0; i < filters[j].length; i++) {
                for (const cond in filters[j][i]) {
                    const qs = [];
                    const condition = filters[j][i][cond];
                    const idxRemoved = [];
                    condition.forEach((c, idx) => {
                        if (c.entity_name) {
                            const entity = entities && entities.find((e) => e.name === c.entity_name);
                            const entityId = entity ? entity.item_id : '00000000-0000-0000-0000-000000000000';
                            if (alreadyContainsEntity1(condition) && !alreadyContainsEntity2(condition)) {
                                condition.splice(idx, 1);
                                condition.push({ entity2_id: entityId });
                            } else if (!alreadyContainsEntity1(condition) && alreadyContainsEntity2(condition)) {
                                condition.splice(idx, 1);
                                condition.push({ entity_id: entityId });
                            } else {
                                condition[idx].entity_id = entityId;
                                delete condition[idx].entity_name;
                                if (activeSelected() && !alreadyContainsActive(condition)) { condition.push({ active: true })}
                                const arr = JSON.parse(JSON.stringify(condition));
                                arr.forEach((i, idx) => {
                                    if (i.entity_id) {
                                        i.entity2_id = entityId;
                                    }
                                    delete i.entity_id;
                                })
                                filters.push([{ [cond]: arr }]);
                            }
                        }
                        if (c.entity_type_name) {
                            const entityType = entity_types && entity_types.find((e) => e.name === c.entity_type_name);
                            const entityTypeId = entityType ? entityType.item_id : 'none';
                            qs.push({'entity_type_id': entityTypeId });
                            idxRemoved.push(idx);
                        }
                        if (c.area_name) {
                            const area = areas && areas.find((a) => a.name === c.area_name);
                            const areaId = area ? area.item_id : 'none';
                            qs.push({'area_id': areaId });
                            idxRemoved.push(idx);
                        }
                        if (c.event_type_name) {
                            const eventType = event_types && event_types.find((e) => e.name === c.event_type_name)
                            const eventTypeId = eventType ? eventType.item_id : 'none';
                            qs.push({'event_type_id': eventTypeId });
                            idxRemoved.push(idx);
                        }
                    })
                    for (let i = idxRemoved.length - 1; i >= 0; i--) {
                        condition.splice(idxRemoved[i], 1);
                    }
                    if (qs.length > 0) {
                        let matchingIds;
                        if (cond === 'EQ') {
                            matchingIds = defs.filter((d) => {
                                const storedEntityTypes = [];
                                for (let i = 0; i < qs.length; i++) {
                                    for (const field in qs[i]) {
                                        if (field === 'entity_type_id') {
                                            storedEntityTypes.push(qs[i][field]);
                                        } else if (d[field] === qs[i][field]) {
                                            continue;
                                        } else {
                                            return false;
                                        } 
                                    }
                                }
                                if (storedEntityTypes.length === 1) {
                                    if (d.entity_type_id === storedEntityTypes[0] || d.entity_type2_id === storedEntityTypes[0]) {
                                        return true;
                                    }
                                    return false;
                                } else if (storedEntityTypes.length === 2) {
                                    if (d.entity_type_id === storedEntityTypes[0] && d.entity_type2_id === storedEntityTypes[1]) {
                                        return true;
                                    } else if (d.entity_type_id === storedEntityTypes[1] && d.entity_type2_id === storedEntityTypes[0]) {
                                        return true;
                                    }
                                    return false;
                                }
                                return true;
                            }).map((n) => n.item_id);
                        } else if (cond === 'NEQ') {
                            matchingIds = defs.filter((d) => {
                                for (let i = 0; i < qs.length; i++) {
                                    let storedEntityTypes = [];
                                    for (const field in qs[i]) {
                                        if (field === 'entity_type_id') {
                                            storedEntityTypes.push(qs[i][field]);
                                        } else if (d[field] !== qs[i][field]) {
                                            continue;
                                        } else {
                                            return false;
                                        } 
                                    }
                                }
                                if (storedEntityTypes.length === 1) {
                                    if (d.entity_type_id !== storedEntityTypes[0] || d.entity_type2_id !== storedEntityTypes[0]) {
                                        return true
                                    }
                                    return false;
                                } else if (storedEntityTypes.length === 2) {
                                    if (d.entity_type_id !== storedEntityTypes[0] && d.entity_type2_id !== storedEntityTypes[1]) {
                                        return true;
                                    } else if (d.entity_type_id !== storedEntityTypes[1] && d.entity_type2_id !== storedEntityTypes[0]) {
                                        return true;
                                    }
                                    return false;
                                }
                                return true;
                            }).map((n) => n.item_id);
                        }
                        if (matchingIds.length > 0) {
                            condition.push({ event_definition_id: matchingIds[0] });
                            for (let l = 1; l < matchingIds.length; l++) {
                                const arr = JSON.parse(JSON.stringify(condition));
                                arr.forEach((i) => {
                                    if (i.event_definition_id) {
                                        i.event_definition_id = matchingIds[l];
                                    }
                                })
                                filters.push([{ [cond]: arr }]);
                            }
                        } else {
                            if (condition.length === 0) {
                                condition.push({ event_definition_id: '' })
                            }
                        }
                        if (activeSelected() && !alreadyContainsActive(condition)) { condition.push({active: true}) };
                    }
                }
            } 
        }
    } else if (activeSelected()){
        theWidget.widget.query.equalTo('active', true);
    } else {
        $('#search-events-input').val('');
    }
    return theWidget.widget;
}

function debounce(func, wait, immediate) {
    let timeout;
    return function() {
        var context = this, args = arguments;
        var later = function() {
            timeout = null;
            if (!immediate) func.apply(context, args);
        };
        var callNow = immediate && !timeout;
        clearTimeout(timeout);
        timeout = setTimeout(later, wait);
        if (callNow) func.apply(context, args);
    };
};

function activeSelected() {
    return $('#activeEventsButton').hasClass('active');
}

function alreadyContainsActive(arr) {
    for (let i = 0; i < arr.length; i++) {
        if(arr[i].hasOwnProperty('active')) {
            return true;
        }
    }
    return false;
}

function alreadyContainsEntity1(arr) {
    for (let i = 0; i < arr.length; i++) {
        if(arr[i].hasOwnProperty('entity_id')) {
            return true;
        }
    }
    return false;
}

function alreadyContainsEntity2(arr) {
    for (let i = 0; i < arr.length; i++) {
        if(arr[i].hasOwnProperty('entity2_id')) {
            return true;
        }
    }
    return false;
}

window.regulateEventGrid = regulateEventGrid;

