function renderItem(a) {
    const item = $('<button></button>')
    item.addClass('list-group-item list-group-item-action')
    if (location.hash === `#/${a.value}`) {
        item.addClass('selected-list-item')
    }
    const itemDesc = $('<span></span>');
    itemDesc.html(a.label)
    item.on('click', () => {
        CB_PORTAL.selectPage(`/${a.value}`)
        CB_PORTAL.toggleFlyout();
    })
    item.append(itemDesc)
    return item
}

