let { title, area_id, entity_id, entity2_id, state_option, ...item } = this.widget.data
const areaName = $('.field-area_id input').val()
const entity2Name = $('.field-entity2_id input').val()

const entText = $('.field-entity_id input').val()
const insideText = $('.field-is_inside option:selected').text()
const thresholdText = $('.field-threshold input').val()
const units = datasources.Views_views.latestData()[0] && datasources.Views_views.latestData()[0].data.unit_display_name
const togetherWord = `${insideText}${thresholdText ? ` ${thresholdText} ${units} to` : ''}`
const endingText = (areaName || entity2Name)
item.name = `${entText} ${togetherWord} ${endingText}`
item.updated_at = (new Date()).toISOString()
item.together_word = togetherWord
// debugger
const eventType = datasources.fetchEventDefinitionTypes.latestData() && datasources.fetchEventDefinitionTypes.latestData().results.DATA.find(d => d.item_id === item.event_type_id) || {}

if (entity2_id === 'All') {
    entity2_id = null
}
if (entity_id === 'All') {
    entity_id = null
}
if (area_id === 'All') {
    area_id = null
}

let entity_type_id;
let entity_type2_id;
if (datasources.Entities_fetchEntityTypes.latestData()) {
    // debugger
    entity_type_id = datasources.Entities_fetchEntityTypes.latestData().results.DATA.find(
        d => d.item_id === entity_id
    ) && entity_id

    entity_type2_id = datasources.Entities_fetchEntityTypes.latestData().results.DATA.find(
        d => d.item_id === entity2_id
    ) && entity2_id
}

const isArea = eventType.name === 'area';

if (entity_type_id) {
    item.entity_type_id = entity_type_id
} else {
    item.entity_id = entity_id
}

let area_type_id;
if (isArea) {
    if (datasources.Home_fetchAreaTypes.latestData()) {
        area_type_id = datasources.Home_fetchAreaTypes.latestData().results.DATA.find(
            d => d.item_id === area_id
        ) && area_id
        if (area_type_id) {
            item.area_type_id = area_id
            item.area_id = null
            // needs to be actively null to clear out any old values
        } else {
            item.area_id = area_id
            item.area_type_id = null
        }
    }
} else {
    if (entity_type2_id) {
        item.entity_type2_id = entity_type2_id
    } else {
        item.entity2_id = entity2_id
    }
}

if (eventType.name !== 'area') {
    if (item.entity_id && item.entity2_id === item.entity_id) {// all to all is okay
        return {
            success: false, results: 'Entities cannot be the same'
        }
    }
}

if (item.item_id) {
    return datasources.EventDefinitions_updateEventDefinitions.sendData({ item }).then(function (res) {
        if (!res.success === false) {
            datasources.EventDefinitions_fetchEventDefinitions.sendData()
            datasources.EventHistory_fetchEventDefinitions.sendData()
            datasources.Home_fetchEventDefinitions.sendData()
        }
        return res
    })
} else {
    return datasources.EventDefinitions_createEventDefinitions.sendData({ item }).then(function (res) {
        if (!res.success === false) {
            datasources.EventDefinitions_fetchEventDefinitions.sendData()
            datasources.EventHistory_fetchEventDefinitions.sendData()
            datasources.Home_fetchEventDefinitions.sendData()
        }
        return res
    })
}

