  d => ({ name: `Any ${d.name}*`, value: d.item_id })
) || [])

const eventDefTypeOptions = datasources.fetchEventDefinitionTypes.latestData() && datasources.fetchEventDefinitionTypes.latestData().results.DATA.filter(
  d => (d.enabled)
).map(
  d => ({ name: d.name, value: d.item_id })
  ) || {}
const units = datasources.Views_views.latestData() && datasources.Views_views.latestData()[0] && datasources.Views_views.latestData()[0].data.unit_display_name


/**
 * @type {FormSourceObj}
 */
var formSourceObj = {
  data: {},
  overrideFieldSettings: {
    entity_id: { dropdownOptions: [{ name: 'Any Entity', value: 'All' }, ...entityOptions, ...entityTypeOptions] },
    entity2_id: { dropdownOptions: [{ name: 'Any Entity', value: 'All' }, ...entityOptions, ...entityTypeOptions] },
    area_id: { dropdownOptions: [{ name: 'Any Area', value: 'All' }, ...areaOptions, ...areaTypeOptions] },
    event_type_id: {
      dropdownOptions: eventDefTypeOptions,
      defaultValue: eventDefTypeOptions[0] && eventDefTypeOptions[0].value
    },
    threshold: {
      smallText: units
    },
    updated_at: {}
  },
  onUpdate: ({ currentValues, fieldSettings }) => {

    const evntType = eventDefTypeOptions.find(d => d.value === currentValues.event_type_id) || {}


    if (evntType.name === 'area') {
      fieldSettings.entity2_id.display = false
      fieldSettings.area_id.display = true
      fieldSettings.is_inside.dropdownOptions = [
        { name: 'is inside', value: true },
        { name: 'is outside of', value: false },
      ]
      fieldSettings.threshold.display = false
      fieldSettings.is_inside.widthPercent = 33
    } else {
      fieldSettings.entity2_id.display = true
      fieldSettings.area_id.display = false
      fieldSettings.threshold.widthPercent = 12
      fieldSettings.is_inside.widthPercent = 22

      fieldSettings.is_inside.dropdownOptions = [
        { name: 'is closer than', value: true },
        { name: 'is farther than', value: false },
      ]
    }
    // debugger
    return { currentValues, fieldSettings }
  }
};

if (datasources.EventDefinitions_current_event_def_id.latestData() !== 'none') {
  formSourceObj.data = (datasources.EventDefinitions_fetchEventDefinitions.latestData().results.DATA.find(
    d => d.item_id === datasources.EventDefinitions_current_event_def_id.latestData()
  ) || {})
  const btnAdd = $('<button id="addEventDefLink" class="btn btn-link d-inline">Add Event Definition</button>')

  const btnHist = $(`
    <button id="eventHistory" data-item-id="${formSourceObj.data.item_id}" class="btn btn-secondary mb-3">
      See event history
    </button>
  `)
  const t = $('<h3>Update Event Definition</h3>')
  const main = $('<div class="titleform"/>')
  main.append(btnHist)
  t.append(btnAdd)

  main.append(t)
  formSourceObj.data.title = main
  formSourceObj.overrideFieldSettings.updated_at.display = true
} else {
  formSourceObj.overrideFieldSettings.updated_at.display = false
  formSourceObj.data.title = $('<h3 class="titleform">New Event Definition</h3>')
}

formSourceObj.data.entity_id = formSourceObj.data.entity_id || formSourceObj.data.entity_type_id
formSourceObj.data.entity2_id = formSourceObj.data.entity2_id || formSourceObj.data.entity_type2_id
formSourceObj.data.area_id = formSourceObj.data.area_id || formSourceObj.data.area_type_id

// account for form bug
if (!formSourceObj.data.entity2_id) {
  formSourceObj.data.entity2_id = ''
}
if (!formSourceObj.data.area_id) {
  formSourceObj.data.area_id = ''
}
if (!formSourceObj.data.entity_id) {
  formSourceObj.data.entity_id = ''
}

const d = formSourceObj.data

if (!formSourceObj.data.entity_id) {
  formSourceObj.data.entity_id = 'All'
}
if (!formSourceObj.data.entity2_id) {
  formSourceObj.data.entity2_id = 'All'
}
if (!formSourceObj.data.area_id) {
  formSourceObj.data.area_id = 'All'
}

if (!window.hasSetUpListenersForEventDefCrudForm) {
  window.hasSetUpListenersForEventDefCrudForm = true
  $('body').on('click', '#eventHistory', (e) => {
    e.preventDefault()
    CB_PORTAL.selectPage('/EventHistory')
    const event_definition_id = e.target.dataset.itemId;
    datasources.EventHistory_filter.sendData({ event_definition_id })
    datasources.EventHistory_fetchEvents.sendData({ event_definition_id })
  })
  $('body').on('click', '#addEventDefLink', (e) => {
    e.preventDefault()
    datasources.EventDefinitions_current_event_def_id.sendData('none')
  })
}

return formSourceObj


