value.JavaScript:
//JavaScript

const AREA_INSERTION_POINT = 1;
const MAP_ID = "entity-position-map";
const entityPositionTopic = "section1/tag/+";
const mapElem = $(`#${MAP_ID}`);
const ASSET_ICON_MAX_SIZE = 25;
const SELECTED_ASSET_COLOR = "red";
const TICK_INTERVAL = 100;
let isDrawing = false;
let editArea;
const saveAreaBtnId = "save-area-btn";
const entityPopover = {
  htmlElem: null,
  domElem: null
};
const drawBtn = $(`#${MAP_ID}-draw`);
const moveBtn = $(`#${MAP_ID}-delete`);

if (!window.entityPositionMap) {
  window.entityPositionMap = {
    isLoaded: false,
    metersToPixelsScale: 1,
    assets: {},
    sections: {},
    anchors: {},
    cb_msg: null
  };
  window.addEventListener("resize", function(event) {
    window.entityPositionMap.stage.update();
  });

  safeDrawMap();

  window.entityPositionMap.cb_msg = CB_PORTAL.ClearBlade.Messaging(
    {
      useSSL: window.location.protocol === "https:"
    },
    () => {
      try {
        window.entityPositionMap.cb_msg.subscribe(
          entityPositionTopic,
          {},
          updateAssetLocation
        );
      } catch (e) {
        console.error("error subscribing to entity position topic", e);
      }
    }
  );

  datasources.Home_selected_entity.latestData.subscribe(selectEntity);
  datasources.Home_selected_area.latestData.subscribe(selectArea);
} else {
  safeDrawMap();
}

function getCoordForMarker(pos) {
  return (
    pos * window.entityPositionMap.metersToPixelsScale - ASSET_ICON_MAX_SIZE / 2
  );
}

function getXForMarker(x_pos) {
  return getCoordForMarker(x_pos);
}

function getYForMarker(y_pos) {
  return getCoordForMarker(y_pos);
}

function getCoordForArea(pos) {
  return pos * window.entityPositionMap.metersToPixelsScale;
}

function getNormalizedPointForArea(canvasPosition) {
  return canvasPosition / window.entityPositionMap.metersToPixelsScale;
}

function getNormalizedXForArea(canvasPosition) {
  return getNormalizedPointForArea(canvasPosition);
}
function getNormalizedYForArea(canvasPosition) {
  return getNormalizedPointForArea(canvasPosition);
}

function getXCoordForArea(x_pos) {
  return getCoordForArea(x_pos);
}

function getYCoordForArea(y_pos) {
  return getCoordForArea(y_pos);
}

function getStageCoordsForArea(coords, dest) {
  if (dest) {
    dest.x = getXCoordForArea(coords.x);
    dest.y = getYCoordForArea(coords.y);
  } else {
    return {
      x: getXCoordForArea(coords.x),
      y: getYCoordForArea(coords.y)
    };
  }
}

function createPopoverBody(name, dismissCb) {
  const container = $("<div></div>");
  // x at the top right of popover
  const xDismissContainer = $("<div></div>").addClass("d-flex");
  const xDismiss = $("<div></div>")
    .html("&times;")
    .addClass("ml-auto c-pointer")
    .on("click", () => {
      dismissCb();
    });
  xDismissContainer.append(xDismiss);
  const body = $("<div></div>").html(
    `<div class="form-group row">
    <label for="entity-name" class="col-sm-4 col-form-label"><b>Entity</b></label>
    <div class="col-sm-8">
      <input type="text" readonly class="form-control-plaintext" id="entity-name" value="${name}">
    </div>
  </div>
    `
  );
  return container.append(xDismissContainer, body);
}

function addPopoverToContainer(coords, addClass = "") {
  const htmlElement = $("<div></div>");
  htmlElement.appendTo(`#${MAP_ID}-container`);
  htmlElement.css({
    // have to set these to 0 in order to position properly
    top: coords.y,
    left: coords.x
  });
  htmlElement.addClass(addClass);
  return htmlElement;
}

function getEntityIdByTagId(tagId) {
  try {
    const tags = datasources.Home_tags.latestData();
    for (let i = 0, len = tags.length; i < len; i++) {
      if (tags[i].data.name === tagId) {
        return tags[i].data.entity_id;
      }
    }
  } catch (e) {
    console.error("Failed to get entity ID by tag ID", e);
  }
  return null;
}

function updateAssetLocation(message) {
  message = JSON.parse(message);
  const tagId = message.name;
  const entityId = getEntityIdByTagId(tagId);
  if (!entityId) {
    console.error(`No entity ID for tag ID '${tagId}'`);
    return;
  }

  const asset = entityPositionMap.assets[entityId];
  if (asset) {
    asset.x = getXForMarker(message.x_pos);
    asset.y = getYForMarker(message.y_pos);
    // updating the stage is handled by our tick function
    // entityPositionMap.stage.update();
  }
}

function hideEntityPopover() {
  if (entityPopover.htmlElem && entityPopover.domElem) {
    entityPopover.htmlElem.popover("hide");
    window.entityPositionMap.stage.removeChild(entityPopover.domElem);
  }
}

function selectEntity(id) {
  const entity = window.entityPositionMap.assets[id];
  if (!entity) {
    console.warn(`No entity with id '${id}'`);
    return;
  }
  hideEntityPopover();

  entityPopover.htmlElem = addPopoverToContainer({
    x: 0,
    y: 0
  });

  entityPopover.htmlElem.popover({
    html: true,
    content: createPopoverBody(entity.name, () => {
      if (datasources.Home_selected_entity.latestData()) {
        datasources.Home_selected_entity.sendData("null");
      }
      hideEntityPopover();
      window.entityPositionMap.stage.update();
    }),
    container: entityPopover.htmlElem,
    trigger: "focus",
    placement: "top"
  });
  entityPopover.htmlElem.popover("show");

  entityPopover.domElem = new createjs.DOMElement(entityPopover.htmlElem[0]);
  entity.addChild(entityPopover.domElem);
  window.entityPositionMap.stage.update();
}

function selectArea(id) {
  const area = window.entityPositionMap.sections[id];
  if (!area) {
    console.warn(`No area with id '${id}'`);
    return;
  }

  for (const areaId in window.entityPositionMap.sections) {
    window.entityPositionMap.sections[areaId].hideInfo();
  }

  area.showInfo();
}

function safeDrawMap() {
  if (
    datasources.Home_views.lastUpdated &&
    datasources.Home_views.latestData()[0]
  ) {
    drawMap(datasources.Home_views.latestData()[0].data);
  } else {
    datasources.Home_views.latestData.subscribe(views => {
      if (views[0]) {
        drawMap(views[0].data);
      }
    });
  }
}

function removeMouseMoveListener() {
  editArea.removeMouseMoveListener();
  editArea = null;
}

function drawMap(mapData) {
  window.entityPositionMap.stage = new createjs.Stage(MAP_ID);
  window.entityPositionMap.stage.enableMouseOver(10);

  window.entityPositionMap.stage.removeAllChildren();
  const bgImg = document.createElement("img");

  bgImg.onload = function() {
    const bg = new createjs.Bitmap(bgImg);
    window.entityPositionMap.stage.canvas.width = bg.image.width;
    window.entityPositionMap.stage.canvas.height = bg.image.height;

    window.entityPositionMap.metersToPixelsScale = determineScale(
      mapData.width,
      mapData.height,
      bg.image.width,
      bg.image.height
    );
    bg.x = 0;
    bg.y = 0;
    // we use a hitarea so that users can use transparent images for the map (easeljs won't register click events if the mouse is clicked on the background of a transparent image)
    const hit = new createjs.Shape();
    hit.graphics
      .beginFill("#000")
      .drawRect(bg.x, 0, bg.image.width, bg.image.height);
    bg.hitArea = hit;
    window.entityPositionMap.stage.addChild(bg);
    drawAssets(mapData.name);
    drawAreas(mapData.name);
    drawAnchors();

    createjs.Ticker.removeAllEventListeners();
    createjs.Ticker.on("tick", handleTick);
    createjs.Ticker.interval = TICK_INTERVAL;
    window.entityPositionMap.stage.update();
  };
  bgImg.src = mapData.image;
}

function handleTick(event) {
  entityPositionMap.stage.update();
}

function drawAssets(mapName) {
  //remove any old assets
  for (const key in window.entityPositionMap.assets) {
    window.entityPositionMap.stage.removeChild(
      window.entityPositionMap.assets[key]
    );
  }
  window.entityPositionMap.assets = {};
  //create new assets
  const allEntities = datasources.Home_entities.latestData();
  const entityTypes = datasources.Home_entity_types.latestData();

  if (allEntities && allEntities.length) {
    allEntities.forEach(ent => {
      if (ent.data.x_pos === null || ent.data.y_pos === null) {
        return;
      }
      const item_id = ent.data.item_id;
      const entityImage = document.createElement("img");

      const container = new createjs.Container();
      container.x = getXForMarker(ent.data.x_pos);
      container.y = getYForMarker(ent.data.y_pos);
      container.meter_x = ent.data.x_pos;
      container.meter_y = ent.data.y_pos;
      container.name = ent.data.name;
      container.on("click", function() {
        datasources.Home_selected_entity.sendData(item_id);
      });
      window.entityPositionMap.assets[
        item_id
      ] = window.entityPositionMap.stage.addChild(container);

      entityImage.onload = function(evt) {
        const newImage = new createjs.Bitmap(evt.target);
        newImage.scale = determineScale(
          newImage.image.width,
          newImage.image.height,
          ASSET_ICON_MAX_SIZE,
          ASSET_ICON_MAX_SIZE
        );
        container.addChild(newImage);
        window.entityPositionMap.stage.update();
      };
      for (let y = 0; y < entityTypes.length; y++) {
        if (entityTypes[y].data.item_id === ent.data.entity_type_id) {
          entityImage.src = entityTypes[y].data.display_icon;
          break;
        }
      }
    });
  }
}

function drawAreas(mapName) {
  for (const sect in window.entityPositionMap.sections) {
    window.entityPositionMap.stage.removeChild(
      window.entityPositionMap.sections[sect].shape
    );
  }
  window.entityPositionMap.sections = {};
  const sections = datasources.Home_areas.latestData();
  for (let i = 0; i < sections.length; i++) {
    const a = new Area(sections[i].data, window.entityPositionMap.stage);
    a.paint();
    window.entityPositionMap.sections[sections[i].data.item_id] = a;
  }
  window.entityPositionMap.stage.update();
}

function drawAnchors() {
  //remove any old anchors
  for (const key in window.entityPositionMap.anchors) {
    window.entityPositionMap.stage.removeChild(
      window.entityPositionMap.anchors[key]
    );
  }
  window.entityPositionMap.anchors = {};
  //create new anchors
  const allAnchors = datasources.Home_anchors.latestData();

  allAnchors.forEach(anc => {
    if (anc.data.x_pos === null || anc.data.y_pos === null) {
      return;
    }
    const item_id = anc.data.item_id;
    const container = new createjs.Container();
    container.x = getXForMarker(anc.data.x_pos);
    container.y = getYForMarker(anc.data.y_pos);
    container.name = anc.data.name;

    window.entityPositionMap.anchors[
      item_id
    ] = window.entityPositionMap.stage.addChild(container);

    const circ = new createjs.Graphics();

    circ.beginStroke("grey");
    circ.setStrokeStyle(1, "round").beginFill("grey");
    circ.drawCircle(0, 0, 7);

    const s = new createjs.Shape(circ);
    container.addChild(s);
  });

  window.entityPositionMap.stage.update();
}

function determineScale(
  originalWidth,
  originalHeight,
  stageWidth,
  stageHeight
) {
  const widthScale = stageWidth / originalWidth;
  const heightScale = stageHeight / originalHeight;
  return Math.min(widthScale, heightScale);
}

const toggleDrawMode = () => {
  if (isDrawing) {
    isDrawing = false;
    $(`#${MAP_ID}`).removeClass();
    removeMouseMoveListener();
  } else {
    isDrawing = true;
    $(`#${MAP_ID}`).addClass("drawing");
    editArea = new Area(
      {
        isNewArea: true
      },
      window.entityPositionMap.stage
    );
  }
};

drawBtn.off("click");
drawBtn.on("click", () => {
  toggleDrawMode();
});

moveBtn.off("click");
moveBtn.on("click", () => {
  for (const id in window.entityPositionMap.sections) {
    window.entityPositionMap.sections[id].toggleMoveMode();
  }
});

function setUpLine(stage) {
  const lineShape = new createjs.Shape();
  stage.addChild(lineShape);
  return lineShape;
}

function drawLine(shape, startCoords, stopCoords) {
  shape.graphics
    .setStrokeDash([10, 5])
    .setStrokeStyle(1)
    .beginStroke("orange")
    .moveTo(startCoords.x, startCoords.y)
    .lineTo(stopCoords.x, stopCoords.y);
}

function parseCoordinates(coords) {
  try {
    return JSON.parse(coords);
  } catch (e) {
    return [];
  }
}

function Area(config, stage) {
  let coordinates = parseCoordinates(config.coordinates);

  let popover = {
    domElem: null,
    htmlElem: null
  };
  let isMoveMode = false;
  let isDragging = false;
  let mouseMoveListener;
  this.removeMouseMoveListener = () => {
    if (mouseMoveListener) {
      stage.off("stagemousemove", mouseMoveListener);
    }
  };

  this.getCoordinates = () => coordinates;

  this.create = a => {
    return datasources.Home_createAreas.sendData({
      item: {
        name: a.name,
        section_color: a.section_color,
        coordinates: a.coordinates
      }
    });
  };

  this.update = a => {
    return datasources.Home_updateAreas.sendData({
      item: {
        item_id: a.item_id,
        name: a.name,
        area_type_id: a.area_type_id,
        section_color: a.section_color,
        coordinates: a.coordinates
      }
    });
  };

  this.delete = () => {
    return datasources.Home_deleteAreas.sendData({
      item_id: config.item_id
    });
  };

  let mouseDownListener;
  let mouseOverListener;
  let mouseOutListener;
  let moveListener;
  let pressUpListener;
  const startPosition = {
    x: null,
    y: null,
    diffX: null,
    diffY: null
  };
  this.toggleMoveMode = () => {
    if (isMoveMode) {
      document.body.style.cursor = "default";
      this.shape.off("mousedown", mouseDownListener);
      this.shape.off("mouseover", mouseOverListener);
      this.shape.off("mouseout", mouseOutListener);
      this.shape.off("pressmove", moveListener);
      this.shape.off("pressup", pressUpListener);
    } else {
      mouseOverListener = this.shape.on("mouseover", () => {
        document.body.style.cursor = "grab";
      });

      mouseOutListener = this.shape.on("mouseout", () => {
        // todo: what happens if we mouseout of one area but are still in another?
        document.body.style.cursor = "default";
      });

      mouseDownListener = this.shape.on("mousedown", e => {
        startPosition.diffX = e.stageX - this.shape.x;
        startPosition.diffY = e.stageY - this.shape.y;
        isDragging = true;
        document.body.style.cursor = "grabbing";
        this.hideInfo();
      });

      moveListener = this.shape.on("pressmove", e => {
        e.target.x = e.stageX - startPosition.diffX;
        e.target.y = e.stageY - startPosition.diffY;
        stage.update();
      });

      pressUpListener = this.shape.on("pressup", e => {
        isDragging = false;
        document.body.style.cursor = "grab";
        const rawX = e.stageX - startPosition.diffX;
        const rawY = e.stageY - startPosition.diffY;
        const diffX =
          getNormalizedXForArea(rawX) - getNormalizedXForArea(startPosition.x);
        const diffY =
          getNormalizedYForArea(rawY) - getNormalizedYForArea(startPosition.y);
        coordinates = parseCoordinates(config.coordinates).map(c => ({
          x: c.x + diffX,
          y: c.y + diffY
        }));
        this.showInfo();
      });
    }
    isMoveMode = !isMoveMode;
  };

  this.showInfo = () => {
    // todo: do some math to display from the top middle of the area
    const attachmentPoint = coordinates[coordinates.length - 1];
    popover.htmlElem = addPopoverToContainer(
      {
        x: getXCoordForArea(attachmentPoint.x),
        y: getYCoordForArea(attachmentPoint.y)
      },
      "area-popover"
    );

    popover.htmlElem.popover({
      html: true,
      content: createAreaForm(
        {
          name: config.name,
          section_color: config.section_color,
          coordinates,
          area_type_id: config.area_type_id,
          isNewArea: config.isNewArea
        },
        a => {
          if (config.isNewArea) {
            const prom = this.create(a);
            CB_PORTAL.Loader.waitFor(prom, saveAreaBtnId);
            prom.then(data => {
              if (data.success) {
                this.hideInfo();
                // htmlElement.popover("hide");
                stage.removeChild(this.editContainer);
                // stage.removeChild(domElem);
                stage.update();
                datasources.Home_areas.sendData().then(drawAreas);
              } else {
                console.error("Error creating area", data);
              }
            });
          } else {
            const prom = this.update({
              ...a,
              item_id: config.item_id
            });
            CB_PORTAL.Loader.waitFor(prom, saveAreaBtnId);
            prom.then(data => {
              debugger;
              if (data.success) {
                this.hideInfo();
                // htmlElement.popover("hide");
                // stage.removeChild(domElem);
                stage.update();
                datasources.Home_areas.sendData().then(drawAreas);
              } else {
                console.error("Error updating area", data);
              }
            });
          }
        },
        () => {
          const prom = this.delete();
          CB_PORTAL.Loader.waitFor(prom, saveAreaBtnId);
          prom.then(data => {
            if (data.success) {
              if (datasources.Home_selected_area.latestData()) {
                datasources.Home_selected_area.sendData("null");
              }
              this.hideInfo();
              // htmlElement.popover("hide");
              // stage.removeChild(domElem);
              stage.update();
              datasources.Home_areas.sendData().then(drawAreas);
            } else {
              console.error("Error deleting area", data);
            }
          });
        },
        () => {
          if (datasources.Home_selected_area.latestData()) {
            datasources.Home_selected_area.sendData("null");
          }
          this.hideInfo();
          stage.removeChild(this.editContainer);
          drawAreas();
          stage.update();
        }
      ),
      container: popover.htmlElem,
      placement: "top",
      trigger: "manual"
    });
    popover.htmlElem.popover("show");

    popover.domElem = new createjs.DOMElement(popover.htmlElem[0]);
    stage.addChild(popover.domElem);
    stage.update();
  };

  this.hideInfo = () => {
    if (popover.htmlElem && popover.domElem) {
      popover.htmlElem.popover("hide");
      stage.removeChild(popover.domElem);
    }
  };

  this.paint = () => {
    const sectionShape = new createjs.Shape();
    // set the x and y for this shape to the global coords. this will be the origin (0, 0) for all subsequent draw commands
    sectionShape.x = getXCoordForArea(coordinates[0].x);
    sectionShape.y = getYCoordForArea(coordinates[0].y);
    startPosition.x = sectionShape.x;
    startPosition.y = sectionShape.y;
    sectionShape.graphics.beginFill(config.section_color);
    for (let i = 1, len = coordinates.length; i < len; i++) {
      // lines are drawn relative to the shape's origin (x and y)
      sectionShape.graphics.lineTo(
        getXCoordForArea(coordinates[i].x) - getXCoordForArea(coordinates[0].x),
        getYCoordForArea(coordinates[i].y) - getYCoordForArea(coordinates[0].y)
      );
    }
    // close the polygon by drawing a line back to the origin
    sectionShape.graphics.lineTo(0, 0);
    this.shape = sectionShape;
    sectionShape.on("click", () => {
      // don't show info if we're dragging since it's handled by the pressup listener
      if (!isDragging && !editArea) {
        datasources.Home_selected_area.sendData(config.item_id);
      }
    });

    // we want to add areas above the hit area but below markers so that users can still click on markers if inside an area
    stage.addChildAt(sectionShape, AREA_INSERTION_POINT);
  };

  if (config.isNewArea) {
    this.editContainer = new createjs.Container();
    stage.addChild(this.editContainer);

    stage.on("click", onCanvasClick.bind(this));

    const originLineShape = setUpLine(this.editContainer);

    function onCanvasClick(e) {
      if (!isDrawing) {
        return;
      }

      // tell the browser we're handling this event
      e.preventDefault();
      e.stopPropagation();

      if (mouseMoveListener) {
        this.removeMouseMoveListener();
      }

      // store coordinates in their normalized form
      const mouseX = getNormalizedXForArea(e.stageX);
      const mouseY = getNormalizedYForArea(e.stageY);
      coordinates.push({ x: mouseX, y: mouseY });

      if (coordinates.length === 2) {
        // since we clean up the origin line on mousemove we need to draw if the user has created two points
        const firstLineShape = setUpLine(this.editContainer);
        drawLine(
          firstLineShape,
          getStageCoordsForArea(coordinates[0]),
          getStageCoordsForArea(coordinates[1])
        );
      }

      const lineShape = setUpLine(this.editContainer);
      const currentMouseCoords = {};
      mouseMoveListener = stage.on("stagemousemove", function(e) {
        originLineShape.graphics.clear();
        lineShape.graphics.clear();
        currentMouseCoords.x = e.stageX;
        currentMouseCoords.y = e.stageY;
        drawLine(
          originLineShape,
          getStageCoordsForArea(coordinates[0]),
          currentMouseCoords
        );
        if (coordinates.length > 1) {
          drawLine(
            lineShape,
            getStageCoordsForArea(coordinates[coordinates.length - 1]),
            currentMouseCoords
          );
        }
        stage.update();
      });

      const circGraphic = new createjs.Graphics();
      circGraphic.beginStroke("orange");
      circGraphic.setStrokeStyle(1, "round").beginFill("orange");
      circGraphic.drawCircle(
        getXCoordForArea(mouseX),
        getYCoordForArea(mouseY),
        5
      );
      const s = new createjs.Shape(circGraphic);

      s.on("click", e => {
        e.preventDefault();
        e.stopPropagation();
        toggleDrawMode();
        this.showInfo();
      });

      this.editContainer.addChild(s);
      stage.update();
    }
  }

  return this;
}

function createAreaForm(area, saveCb, deleteCb, dismissCb) {
  const areaForm = $("<form></form>");

  // x at the top right of popover
  const xDismissContainer = $("<div></div>").addClass("d-flex");
  const xDismiss = $("<div></div>")
    .html("&times;")
    .addClass("ml-auto c-pointer")
    .on("click", () => {
      dismissCb();
    });
  xDismissContainer.append(xDismiss);

  // area fields
  const nameGroup = $("<div></div>").addClass("form-group");
  const nameLabel = $("<label></label>").text("Name");
  const nameField = $("<input/>")
    .addClass("form-control")
    .val(area.name || "");

  const areaTypeGroup = $("<div></div>").addClass("form-group");
  const areaTypeLabel = $("<label></label>").text("Area Type");
  const areaTypeField = $(`
  <select>
    ${datasources.Home_fetchAreaTypes.latestData()
      .results.DATA.map(d => `<option value="${d.item_id}">${d.name}</option>`)
      .join("")}
  </select>`)
    .addClass("form-control")
    .val(area.area_type_id || "");

  const colorGroup = $("<div></div>").addClass("form-group");
  const colorLabel = $("<label></label>").text("Color");
  const colorField = $("<input/>")
    .attr("type", "color")
    .val(rgb2hex(area.section_color) || null);
  const coordGroup = $("<div></div>").addClass("form-group");
  const coordLabel = $("<label></label>").text("Coordinates");
  const coordField = $("<input/>")
    .addClass("form-control")
    .val(JSON.stringify(area.coordinates) || "[]");
  nameGroup.append(nameLabel, nameField);
  areaTypeGroup.append(areaTypeLabel, areaTypeField);
  colorGroup.append(colorLabel, colorField);
  coordGroup.append(coordLabel, coordField);

  // buttons
  const buttonContainer = $("<div></div>").addClass("d-flex");
  if (!area.isNewArea) {
    const deleteBtn = $("<button>Delete Area</button>")
      .addClass("btn btn-danger")
      .on("click", e => {
        e.preventDefault();
        if (window.confirm(`Are you sure you want to delete '${area.name}'?`)) {
          deleteCb();
        }
      });
    buttonContainer.append(deleteBtn);
  }
  const dismissBtn = $("<button>Cancel</button>")
    .attr("type", "button")
    .addClass("btn ml-auto")
    .on("click", e => {
      e.preventDefault();
      dismissCb();
    });
  const saveBtn = $(
    `<button id='${saveAreaBtnId}'>${
      area.isNewArea ? "Create" : "Update"
    }</button>`
  )
    .addClass("btn btn-primary")
    .attr("type", "submit")
    .on("click", e => {
      e.preventDefault();
      saveCb({
        name: nameField.val(),
        area_type_id: areaTypeField.val(),
        section_color: hexToRgbA(colorField.val(), 0.5),
        coordinates: coordField.val()
      });
    });
  buttonContainer.append(dismissBtn, saveBtn);

  areaForm.append(
    xDismissContainer,
    nameGroup,
    areaTypeGroup,
    colorGroup,
    coordGroup,
    buttonContainer
  );
  return areaForm;
}

function hexToRgbA(hex, alpha) {
  var c;
  if (/^#([A-Fa-f0-9]{3}){1,2}$/.test(hex)) {
    c = hex.substring(1).split("");
    if (c.length == 3) {
      c = [c[0], c[0], c[1], c[1], c[2], c[2]];
    }
    c = "0x" + c.join("");
    return `rgba(${[(c >> 16) & 255, (c >> 8) & 255, c & 255].join(
      ","
    )},${alpha})`;
  }
  throw new Error("Bad Hex");
}

//Function to convert hex format to a rgb color
function rgb2hex(rgb) {
  if (rgb) {
    rgb = rgb.match(
      /^rgba?[\s+]?\([\s+]?(\d+)[\s+]?,[\s+]?(\d+)[\s+]?,[\s+]?(\d+)[\s+]?/i
    );
    return rgb && rgb.length === 4
      ? "#" +
          ("0" + parseInt(rgb[1], 10).toString(16)).slice(-2) +
          ("0" + parseInt(rgb[2], 10).toString(16)).slice(-2) +
          ("0" + parseInt(rgb[3], 10).toString(16)).slice(-2)
      : "";
  }
  return null;
}

