  d.event_id === datasources.EventHistory_current_event_id.latestData()
)) || {}
const entity = datasources.EventDefinitions_fetchEntities.latestData() && datasources.EventDefinitions_fetchEntities.latestData().results.DATA.find(
  d => d.item_id === eventData.entity_id
) || {}
const entity2 = datasources.EventDefinitions_fetchEntities.latestData() && datasources.EventDefinitions_fetchEntities.latestData().results.DATA.find(
  d => d.item_id === eventData.entity2_id
) || {}
const entity_name = entity.name
const entity2_name = entity2.name


const data = this.datasource.results.DATA.map(d => {
  const entity_data = tryParse(d.entity_data)
  if (d.entity2_data) {
    var entity2_data = tryParse(d.entity2_data)
  }
  return {
    ...d,
    // timestamp: dateFns.format(d.timestamp, 'MMM D YYYY, HH:mm:ss'),
    entity_data: entity_data ? `x: ${entity_data.x_pos}, y: ${entity_data.y_pos}` : '',
    entity2_data: entity2_data ? `x: ${entity2_data.x_pos}, y: ${entity2_data.y_pos}` : '',
  }
})

const entity_data = data[0] && tryParse(data[0].entity_data)
const entity2_data = data[0] && tryParse(data[0].entity2_data)

/** @type {GridSourceObj} */
